-- Update the Exception email template
UPDATE dastrax_dev.EMAILTEMPLATE SET `HTML` = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html xmlns="http://www.w3.org/1999/xhtml"> <head> <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" /> </head> <body> #foreach($variable in $variablesList) <br /> DATE: $variable.date <br /> TIME: $variable.time <br /> User: $variable.user <br /> Stage: $variable.stage <br /> EXCEPTION NAME: $variable.exception_name <br /> EXCEPTION MESSAGE: $variable.exception_message <br /> EXCEPTION STACK: $variable.exception_stack <br /> #end </body> </html>', `PLAINTEXT` = '#foreach($variable in $variablesList) DATE: 2013-08-10 TIME: 10:14 LINK: $variable.link PIN: $variable.pin #end' WHERE ID = 'EXCEPTION';

