t;} /* --------------------------------------------------- RESPONSIVENESS Nuke it from orbit. It''s the only way to be sure. ------------------------------------------------------ */ /* Set a max-width, and make it display as block so it will automatically stretch to that width, but will also shrink down on a phone or something */ .container { display:block!important; max-width:600px!important; margin:0 auto!important; /* makes it centered */ clear:both!important; } /* This should also be a block element, so that it will fill 100% of the .container */ .content { padding:15px; max-width:600px; margin:0 auto; display:block; } /* Let''s make sure tables in the content area are 100% wide */ .content table { width: 100%; } /* Odds and ends */ .column { width: 300px; float:left; } .column tr td { padding: 15px; } .column-wrap { padding:0!important; margin:0 auto; max-width:600px!important; } .column table { width:100%;} .social .column { width: 280px; min-width: 279px; float:left; } /* Be sure to place a .clear element after each set of columns, just to be safe */ .clear { display: block; clear: both; } /* ------------------------------------------- PHONE For clients that support media queries. Nothing fancy. -------------------------------------------- */ @media only screen and (max-width: 600px) { a[class="btn"] { display:block!important; margin-bottom:10px!important; background-image:none!important; margin-right:0!important;} div[class="column"] { width: auto!important; float:none!important;} table.social div[class="column"] { width:auto!important; } } </style> </head> <body bgcolor="#FFFFFF"> #foreach($variable in $variablesList) <br /> <!-- HEADER --> <table class="head-wrap" bgcolor="#C6D146"> <tr> <td></td> <td class="header container" > <div class="content"> <table bgcolor="#C6D146"> <tr> <td><h6 class="collapse">New Account Requested</h6></td> <td align="right"><h6 class="collapse">Jnoc</h6></td> </tr> </table> </div> </td> <td></td> </tr> </table><!-- /HEADER --> <!-- BODY --> <table class="body-wrap"> <tr> <td></td> <td class="container" bgcolor="#FFFFFF"> <div class="content"> <table> <tr> <td> <h3>Hi,</h3> <p class="lead">A new Jnoc account has been requested. Their details are as follows;</p> <!-- Callout Panel --> <p class="callout" align="center"> NAME: $variable.name <br /> Company: $variable.company <br /> Email: $variable.email <br /> Phone: $variable.telephone <br /> </p><!-- /Callout Panel --> <p>Thanks<br/>The 555 team<br/><br/></p> <!-- social & contact --> <table class="social" width="100%"> <tr> <td> <!-- column 1 --> <table align="left" class="column"> <tr> <td> <h5 class="">Visit:</h5> <p>617 N. Mary Ave.<br/> Sunnyvale CA 94085</p> </td> </tr> </table><!-- /column 1 --> <!-- column 2 --> <table align="left" class="column"> <tr> <td> <h5 class="">Contact Info:</h5> <p>Phone: <strong>888.409.9997</strong><br/> Email: <strong><a href="emailto:support@solidtechusa.com">support@solidtechusa.com</a></strong></p> </td> </tr> </table><!-- /column 2 --> <span class="clear"></span> </td> </tr> </table><!-- /social & contact --> </td> </tr> <tr> <td> </td> </tr> </table> </div><!-- /content --> </td> <td></td> </tr> </table><!-- /BODY --> <!-- FOOTER --> <table class="footer-wrap"> <tr> <td></td> <td class="container"> <!-- content --> <div class="content"> <table> <tr> <td align="center"> </td> </tr> </table> </div><!-- /content --> </td> <td></td> </tr> </table><!-- /FOOTER --> #end </body> </html>', '/confirm/account.jsf', '#foreach($variable in $variablesList) NAME: $variable.name COMPANY: $variable.company EMAIL: $variable.email PHONE: $variable.telephone #end', 'New Account Requested', 'New Account Requested');

